package kgc.cn.role.sys.model;

public class Course {

	public Course() {
		// TODO Auto-generated constructor stub
	}
	//课程编号
	private int number;
	// 课程名字
	private String name;
	// 课时
	private int times;
	// 学分
	private int score;
	public int getNumber() {
		return number;
	}
	public void setNumber(int number) {
		this.number = number;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getTimes() {
		return times;
	}
	public void setTimes(int times) {
		this.times = times;
	}
	public int getScore() {
		return score;
	}
	public void setScore(int score) {
		this.score = score;
	}

	


}
